import React, { useState } from "react";
import Logo from "../Images/logo.png"
// import Translogo from "../Images/transLogo.png";
import { NavLink } from "react-router-dom";
import "../Css/TransparentHeader.css";
import "../Fonts/Isidora/IsidoraSans-Regular.ttf";
import { HiOutlineBars3, HiOutlineXMark } from "react-icons/hi2";

const TransparentHeader = () => {
  const [header, setHeader] = useState(false);
  const changeBackground = () => {
    if (window.scrollY >= 580) {
      setHeader(true);
    }
    else  {
      setHeader(false);
    }
  
  };
  window.addEventListener("scroll", changeBackground);

  const [isMobile, setIsMobile] = useState(false);
    
  
  return (
    <>

      <header
        className={header ? "transparentHeader  navbarr fixed" : "transparentHeader"}
      >
        <div class="navbar navbar-expand-lg navbar-menu-container container d-flex justify-content-around">
          <div className="logo BlackLogo nav-logo-container">
            <img src={Logo} alt="aivys-logo" />
          </div>
        
          
            <nav className="small-screen d-flex justify-content-between align-items-center lists">
            <div className={isMobile ? "nav-links-mobile" : "  nav-links"}>
              <NavLink
                exact
                to="/"
                className="listItem2 listItem3 mx-3 active"
                activeClassName="active"
              >
                Home
              </NavLink>
              <NavLink
                to="/about"
                className="listItem2 listItem3 mx-3 active"
                activeClassName="active"
              >
                About
              </NavLink>
              <NavLink
                to="/couture"
                className="listItem2 listItem3 mx-3 active"
                activeClassName="active"
              >
                Couture Process
              </NavLink>
              <NavLink
                to="/store"
                className="listItem2 listItem3 mx-3 active"
                activeClassName="active"
              >
                Store
              </NavLink>
              <NavLink
                to="/contact"
                className="listItem2 listItem3 mx-3 active"
                activeClassName="activeItem"
              >
                Contact
              </NavLink>
            </div>
      

          <div>
            <a className="btnText" href="/bookappointment">
              <button className="transparentheader-button">
                Book a Consultation
              </button>
            </a>
          </div>
            </nav>

          <div className="mobile-menu-icon">
          {isMobile ? (
            <HiOutlineXMark style={{ color: '#fff', fontSize: '35px' }}/>
            ) : (
              <HiOutlineBars3 style={{ color: '#fff', fontSize: '35px' }}/>
            )}
        </div>
        
        </div>

        
      </header>
    </>
  );
};
export default TransparentHeader;
