import React, { useState, useRef } from "react";
import contactImage from "../Images/image1.png";
import "../Css/Contact.css";
import Checkbox from "@mui/material/Checkbox";
import PhoneInput from "react-phone-input-2";
import "react-phone-input-2/lib/style.css";

const Contact = () => {
  const label = { inputProps: { "aria-label": "Checkbox" } };

  // const [phone, setPhone]= useState('');
  // // const phone= useRef();
  // console.log(phone.current);

  const [value, setValue] = useState();

  return (
    <>
      <div className="container text-center mt-5">
        <h3 className="pt-5 text-uppercase contact-heading">Contact us</h3>
        <p className="contact-text">We'd love to hear from you.</p>
        <p className="contact-tiny-text">
          Our friendly team is always here to chat.
        </p>
      </div>
      <div>
        <img src={contactImage} alt="contactImage" className="contactImage" />
      </div>

      <section className="section mt-5">
        <div className="container">
          <div className="card shadow">
            <div className="card-body">
              <div className="row">
                <div className="form-group col-md-6">
                  <label className="mb-1">Name</label>
                  <input
                    type="text"
                    className="form-control mb-4"
                    placeholder="Your Name"
                  />
                </div>

                <div className="form-group col-md-6">
                  <label className="mb-1">Email</label>
                  <input
                    type="email"
                    className="form-control"
                    placeholder="you@company.com"
                  />
                </div>

                {/* <PhoneInput
                    country={'us'}
                    value='hello'
                    // ref={phone}
                    onChange={(e) => setPhone(e.target.value)}
                /> */}

                <PhoneInput
                  // placeholder="Enter phone number"
                  value={value}
                  country={"us"}
                  onChange={setValue}
                />

                <div className="form-group col-md-6">
                  <label className="mb-1">Services</label>
                  <Checkbox {...label} />
                  <Checkbox {...label} />
                  <Checkbox {...label} disabled />
                  <Checkbox {...label} disabled checked />
                </div>

                <div className="form-group col-md-12 ">
                  <label className="mb-1">How can we help?</label>
                  <textarea
                    rows="3"
                    className="form-control"
                    placeholder="Tell us a little about the project."
                  ></textarea>
                </div>
                <div className="form-group py-3">
                  <button
                    type="button"
                    className="btn btn-success shadow w-100"
                  >
                    Send request
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};
export default Contact;
