import React from "react";
import contactImage from "../Images/image1.png";
import layer from "../Images/layer.png";
import "../Css/Couture.css";

const Couture = () => {
  return (
    <>
      <div className="container text-center mt-5">
        <h3 className="pt-5 text-uppercase couture-heading">
          Fashion Design Process
        </h3>
        <p className="contact-text text-capitalize couture-text">
          From briefs to assembly
        </p>
        <p className="couture-tiny-text">
          Once the dress is fully finished, you can pick up at the studio or we
          can <br /> arrange a delivery to you anywhere in the world.
        </p>
      </div>

      <div className="layer">
        {/* <img src={contactImage} alt="contactImage" className="contactImage" /> */}

        <div className="blur-box">
          <h1>1. Consultation</h1>
          <p className="">
            The first stage is to discuss with you about your dress, all the
            details of the dress are discussed in full and take note of. You’ll
            be asked questions such as the preferred colour, closure type,
            fabric choice, embellishment, silhouette choice and so. You’ll be
            expected to have a few pictures or videos to define your taste to us
            but do not worry if you’re unsure, we’ll assist you. The
            consultation can be done in different ways: In person at the store,
            E-mail, WhatsApp chat, Video call on WhatsApp in person at our
            store, via a google meet or WhatsApp call. Consultation with our
            Bridal consultant is free but comes ata fee with Evi.
          </p>
        </div>
      </div>

      <div>
        {/* <img src={contactImage} alt="contactImage" className="contactImage" /> */}
      </div>
      <div>
        {/* <img src={contactImage} alt="contactImage" className="contactImage" /> */}
      </div>
      <div>
        {/* <img src={contactImage} alt="contactImage" className="contactImage" /> */}
      </div>
      <div>
        {/* <img src={contactImage} alt="contactImage" className="contactImage" /> */}
      </div>
      <div>
        {/* <img src={contactImage} alt="contactImage" className="contactImage" /> */}
      </div>
    </>
  );
};
export default Couture;
