import React, { useState } from "react";
import logo from "../Images/logo.png";
import { NavLink } from "react-router-dom";
import "../Css/TransparentHeader.css";
import "../Fonts/Isidora/IsidoraSans-Regular.ttf";
import { HiOutlineBars3 } from "react-icons/hi2";
import Box from "@mui/material/Box";
import Drawer from "@mui/material/Drawer";
import List from "@mui/material/List";
import Divider from "@mui/material/Divider";
import ListItem from "@mui/material/ListItem";
import ListItemButton from "@mui/material/ListItemButton";
import ListItemIcon from "@mui/material/ListItemIcon";
import ListItemText from "@mui/material/ListItemText";
import HomeIcon from "@mui/icons-material/Home";
import InfoIcon from "@mui/icons-material/Info";
import CommentRoundedIcon from "@mui/icons-material/CommentRounded";
import PhoneRoundedIcon from "@mui/icons-material/PhoneRounded";

const TransparentHeader = () => {
  const [header, setHeader] = useState(false);
  const changeBackground = () => {
    if (window.scrollY >= 470) {
      setHeader(true);
    } else {
      setHeader(false);
    }
  };
  const [openMenu, setOpenMenu] = useState(false);
  const menuOptions = [
    {
      text: "Home",
      icon: <HomeIcon />,
    },
    {
      text: "Product",
      icon: <InfoIcon />,
    },
    {
      text: "Category",
      icon: <CommentRoundedIcon />,
    },
    {
      text: "Utility",
      icon: <PhoneRoundedIcon />,
    },
  ];
  window.addEventListener("scroll", changeBackground);
  return (
    <>
      <Navbar
        expand="md"
        className={header ? "transparentHeader d-flex  active" : "transparentHeader"}
      >
          <Navbar.Brand className="">
            <div className="logo">
              <img src={logo} alt="aivys-logo" />
            </div>
          </Navbar.Brand>

          <Navbar.Toggle aria-controls="navbarScroll" />
          <Navbar.Collapse id="navbarScroll">
            <Nav
              className="me-auto my-2 my-lg-0"
              style={{ maxHeight: "100px" }}
              navbarScroll
            >
              <NavLink
                exact
                to="/"
                className="listItem2 mx-3 active"
                activeClassName="active"
              >
                Home
              </NavLink>
              <NavLink
                to="/about"
                className="listItem2 mx-3 active"
                activeClassName="active"
              >
                About
              </NavLink>
              <NavLink
                to="/couture"
                className="listItem2 mx-3 active"
                activeClassName="active"
              >
                Couture Process
              </NavLink>
              <NavLink
                to="/store"
                className="listItem2 mx-3 active"
                activeClassName="active"
              >
                Store
              </NavLink>
              <NavLink
                to="/contact"
                className="listItem2 mx-3 active"
                activeClassName="active"
              >
                Contact
              </NavLink>
            </Nav>
        
              <Button
                variant="outline-success" href="/bookappointment"
                className="transparentheader-button"
              >
                Book a Consultation
              </Button>
        
          </Navbar.Collapse>
      </Navbar>

    </>
  );
};
export default TransparentHeader;
