import React, { useState } from "react";
import logo from "../Images/logo.png";
import { NavLink } from "react-router-dom";
import "../Css/TransparentHeader.css";
import "../Fonts/Isidora/IsidoraSans-Regular.ttf";
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';

const TransparentHeader = () => {
  const [header, setHeader] = useState(false);
  const changeBackground = () => {
    if (window.scrollY >= 470) {
      setHeader(true);
    } else {
      setHeader(false);
    }
  
  };
  window.addEventListener("scroll", changeBackground);
  return (
    <>

    <Navbar expand="lg" className={header ? "transparentHeader active" : "transparentHeader"}>
      <Container fluid>
        <Navbar.Brand href="#">
        <div className="logo">
            <img src={logo} alt="aivys-logo" />
          </div>
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="navbarScroll" />
        <Navbar.Collapse id="navbarScroll">
          <Nav
            className="me-auto my-2 my-lg-0"
            style={{ maxHeight: '100px' }}
            navbarScroll
          >
            <Nav.Link exact
                to="/"
                className="listItem2 mx-3 active"
                activeClassName="active"> Home </Nav.Link>
            <Nav.Link to="/about"
                className="listItem2 mx-3 active"
                activeClassName="active">About</Nav.Link>
                <Nav.Link to="/couture"
                className="listItem2 mx-3 active"
                activeClassName="active">Couture Process</Nav.Link>
                <Nav.Link to="/store"
                className="listItem2 mx-3 active"
                activeClassName="active">Store</Nav.Link>
                                <Nav.Link to="/contact"
                className="listItem2 mx-3 active"
                activeClassName="active">Contact</Nav.Link>
            
          </Nav>
          <Form className="btnText" href="/bookappointment">
            
            <Button variant="outline-success" className="transparentheader-button">Book a Consultation</Button>
          </Form>
        </Navbar.Collapse>
      </Container>
    </Navbar>
          

          <nav>
            <div className="lists">
             
              
            </div>
          </nav>

    </>
  );
};
export default TransparentHeader;
